create table ISSUES (
	id INT PRIMARY KEY,
	USER_ID INT,
	BOOK_ID INT,
	ISBN BIGINT,
	BORROW_DATE DATE,
	RETURN_DATE DATE,
	FINE_DUE DECIMAL(4,2),
	IS_FINE_PAID BOOLEAN,
	FOREIGN KEY(USER_ID) references USERS(id),
	FOREIGN KEY(BOOK_ID) references BOOK_COPIES(id),
	FOREIGN KEY(ISBN) references BOOKS(isbn)
);
